
"use strict"




var commons = { }
var request = require('request')





commons.callServer = (url, callback) => {

	request({url, followRedirect: false, agent: false}, (err, res, body) => {

		setTimeout(callback, 0, {
			err,
			statusCode: res ? res.statusCode : null,
			url
		})

	})

}





module.exports = commons
