
"use strict"




var URL        = require('url')
var expect     = require('chai').expect

var commons    = require('bang-test/commons/commons')
var constants  = require('bang-test/commons/constants')

var deploy     = require('bang-test/tests/fuzz/deploy')
var servers    = require('bang-test/tests/fuzz/server-config')




var message = "server redirect with redirect-patterns (n = " + commons.sampleSize(servers.withPatterns) + ")"





describe(message, function ( ) {

	var state
	this.timeout(servers.withPatterns.duration)

	before(done => {

		deploy.overAllEngines(servers.withPatterns, result => {

			state = result
			done( )

		})

	})

	it('redirects without errors', done => {

		state.results.forEach( ({engine, pattern, result}) => {

			expect(result.err).to.be.a('null', result.toString( ))
			expect(result.headers).to.be.an('object', result.toString( ))
			expect(result.headers.location).to.be.a('string', result.toString( ))

			expect(result.statusCode).to.equal(constants.status.REDIRECT, result.toString( ))

		})

		done( )

	})

	it('redirects to the expected host', done => {

		state.results.forEach( ({engine, pattern, result}) => {

			var expected = URL.parse(engine.searchTemplate).host
			var redirect = URL.parse(result.headers.location).host

			expect(expected).to.equal(redirect)

		})

		done( )

	})

	after(done => {

		state.server.server.close(( ) => {
			setTimeout(done, constants.times.SERVER_CLOSE)
		})

	})

})
