
"use strict"





var redirect = require('bang/redirect')




var search = (config, req, res) => {

	var rawQuery = req.query.q

	if (/!about/.test(rawQuery)) {
		redirect.toHelpFile(config, rawQuery, res)
	} else {
		redirect.toSearchEngine(config, rawQuery, res)
	}

}





module.exports = search
